    val listData: ArrayList<Seni>
        get() {
            val list = arrayListOf<Seni>()
            for (position in seniNames.indices){
                val seni = Seni()
                seni.name = seniNames[position]
                seni.detail = seniDetails[position]
                seni.photo = seniImage[position]
                list.add(seni)
            }
            return list
        }

